package reviewClass;

public class SpasePyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=5; i++) {
			
			for (int j=5-1;j>=1;j--) {
				System.out.print("");
				
			}
			
			for (int m= 1; m <= i; m++) {
				System.out.print(m);
			}
			System.out.println();
				
				
			}
		}

	}


